import React, { useState } from 'react';
import ListImages from './images'

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            term: '',
            img: '',
            images: []
        };
    }

    onChange = (event) => {
        this.setState({ term: event.target.value });
    }

    changeStateValue = (data) => {
        this.setState({
            term: '',
            img: data.data[0].images.fixed_height.url,
            //images : [...this.state.images,data.data],
            //images : this.state.images.concat(data.data)
            images: data.data
        });
        console.log(this.state);
    }
    handleSubmit = (event) => {
        event.preventDefault();
        const api_key = 'sLZYU7lvnhUT11k7JVn3YdiN8DmGaYli';
        const url = `http://api.giphy.com/v1/gifs/search?q=${this.state.term}&api_key=${api_key}`;
        fetch(url)
            .then(response => response.json())
            .then(data =>
                this.changeStateValue(data)
            )
            .catch(e => console.log('error', e));
    }
    render() {


        return (
            <>
                <div className="App">
                    <form onSubmit={this.handleSubmit}>
                        <br />
                        <br />
                        <input value={this.state.term} onChange={this.onChange} />
                        <button margin="6px">Search</button>
                    </form>
                    <img src={this.state.img} height="200" alt={this.state.term} />
                    <br />
                    <hr></hr>
                    <ListImages allData={this.state.images} />

                </div>
            </>
        );
    }
}

export default App;
