import React, { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import EventNoteIcon from '@material-ui/icons/EventNote';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import SimpleTabs from './Tab';

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: 'darkblue'
    },
    form: {
        width: '100%',
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
        border: '2px  outset blue',
    },
}));

export default function InformationForm() {
    const classes = useStyles();
    const [state, setState] = useState({
        firstName: '',
        lastName: '',
        education: '',
        email: '',
        mobileNumber: '',
        submit: [],
        dataRecords: []
    })

    function myChangeHandler(event) {
        setState({
            ...state,
            [event.target.id]: event.target.value,
        });
        console.log(state);
    }
    function submitFrom(event) {
        event.preventDefault();
        setState({
            ...state,
            dataRecords: [...state.dataRecords,
            {
                firstName: state.firstName,
                lastName: state.lastName,
                education: state.education,
                email: state.email
            }]
        });
    }

    return (
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
            <Container component="main" maxWidth="xs">
                <CssBaseline />
                <Grid item xs={false} sm={12} md={12} ></Grid>
                <Grid item xs={false} sm={4} md={7}>
                    <div className={classes.SimpleTabscontainers}>
                        <SimpleTabs state={state} handleRemoval={handleRemoval} handleChangeStatus={handleChangeStatus} />
                    </div>
                </Grid>
                <div className={classes.paper}>
                    <Avatar className={classes.avatar} onSubmit={submitFrom}>
                        <EventNoteIcon />
                    </Avatar>
                    <Typography component="h1" variant="h5">
                        Student InFormation
                </Typography>
                    <form className={classes.form} >
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="firstName"
                            label="First Name"
                            name="firstname"
                            onChange={myChangeHandler}
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            name="lastname"
                            label="Last Name"
                            id="lastName"
                            onChange={myChangeHandler}
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            name="education"
                            label="Education"
                            id="education"
                            onChange={myChangeHandler}
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            name="email"
                            type="email"
                            required
                            label="Email"
                            id="email"
                            onChange={myChangeHandler}
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            name="mobilenumber"
                            label="Mobile Number"
                            id="mobileNumber"
                            onChange={myChangeHandler}
                        />
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            color="primary"
                            className={classes.submit}
                        >
                            Submit
                  </Button>
                    </form>
                </div>
            </Container>
        </Grid>
    );
}
