import React, { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
// import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Grid from '@material-ui/core/Grid';
import Select from '@material-ui/core/Select';
import EventNoteIcon from '@material-ui/icons/EventNote';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import SimpleTabs from './Tab';

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(10, 7),
        maxWidth: '900px',
        top: '61px !important',
        margin: '0px 0px 0px 16px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },

    root: {
        maxWidth: '900px',
        position: 'relative',
        margin: '60px auto 0 auto',
        marginBottom: '5px',
        borderColor: 'black',
        border: '6px outset black'
    },

    avatar: {
        margin: theme.spacing(1),
        backgroundColor: 'black',
    },
    form: {
        width: '100%',
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
        border: '2px outset blue'
    },

}));

export default function InformationForm() {
    const classes = useStyles();
    const [state, setState] = useState({
        firstName: '',
        lastName: '',
        email: '',
        qualification: '',
        mobileNumber: '',
        submit: [],
        allRecords: [],
    }
    )

    function handleRemoval(fromId) {

        var form = state.allRecords;
        form = form.filter(function (el) {
            return el.counterId !== fromId;
        });
        setState({
            ...state,
            allRecords: form
        });
        return;
    }


    function myChangeHandler(event) {

        setState({
            ...state,
            [event.target.id]: event.target.value,
        });
        console.log(state);
    }


    function submitForm(event) {
        event.preventDefault();

        setState({
            ...state,
            allRecords: [...state.allRecords,
            {
                firstName: state.firstName,
                lastName: state.lastName,
                email: state.email,
                mobileNumber: state.mobileNumber,
                qualification: state.qualification
            }
            ]
        });
    }

    return (
        <>
            <Grid container component="main"
                justify="center">
            </Grid>

            <Grid container component="main" className={classes.root}
                justify="center">
                <CssBaseline />
                <Grid item xs={false} sm={12} md={12} ></Grid>
                <Grid item xs={false} sm={4} md={7}>
                    <div className={classes.SimpleTabscontainers}>
                        <SimpleTabs state={state} />
                    </div>
                </Grid>
                <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                    <Container component="main" maxWidth="xs">
                        <CssBaseline />
                        <div className={classes.paper}>
                            <Avatar className={classes.avatar}>
                                <EventNoteIcon />
                            </Avatar>
                            <Typography component="h1" variant="h5">
                                Registration Form
                                           </Typography>
                            <form className={classes.form} onSubmit={submitForm}>
                                <Grid container spacing={2}>
                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            autoComplete="fname"
                                            name="firstname"
                                            variant="outlined"
                                            fullWidth
                                            required
                                            id="firstName"
                                            label="First Name"
                                            onChange={myChangeHandler}

                                        />
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            variant="outlined"
                                            fullWidth
                                            required
                                            id="lastName"
                                            label="Last Name"
                                            name="lastname"
                                            autoComplete="lname"
                                            onChange={myChangeHandler}
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            variant="outlined"
                                            fullWidth
                                            required
                                            id="email"
                                            name="email"
                                            label="Email"
                                            type="email"
                                            required
                                            autoComplete="email"
                                            onChange={myChangeHandler}
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            variant="outlined"
                                            fullWidth
                                            required
                                            id="qualification"
                                            name="qualification"
                                            label="Qualification "
                                            autoComplete="qualification"
                                            onChange={myChangeHandler}
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            variant="outlined"
                                            fullWidth
                                            required
                                            id="mobileNumber"
                                            name="mobilenumber"
                                            label="Mobile Number"
                                            autoComplete="mobilenumber"
                                            onChange={myChangeHandler}
                                        />
                                    </Grid>
                                </Grid>
                                <Button
                                    type="submit"
                                    fullWidth
                                    variant="contained"
                                    color="primary"
                                    className={classes.submit} >Submit</Button>
                            </form>
                        </div>
                    </Container >
                </Grid>
            </Grid>
        </>
    );
}
