import React from 'react';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            term: '',
            img: '',
            images : []
        };
    }

    onChange = (event) => {
        this.setState({ term: event.target.value });
    }
    handleSubmit = (event) => {
        event.preventDefault();
        const api_key = 'sLZYU7lvnhUT11k7JVn3YdiN8DmGaYli';
        const url = `http://api.giphy.com/v1/gifs/search?q=${this.state.term}&api_key=${api_key}`;
        fetch(url)
            .then(response => response.json())
            .then(data => this.setState({ term: '', img: data.data[0].images.fixed_height.url,

            images : [...this.state.images,data.data]
        })).then(

            console.log(this.state.images)
        )
            .catch(e => console.log('error', e));
    }

    render() {
        return (
            <div className="App">
                <form onSubmit={this.handleSubmit}>
                    <input value={this.state.term} onChange={this.onChange} />
                    <button>Search</button>
                </form>
                <img src={this.state.img} height="200" alt={this.state.term} />
                <br/>
                <hr></hr>

                {this.state.images.map=(item,index)=>


                <img src={item.images.fixed_height.url} height="200" alt={this.state.term} />



                }


            </div>
        );
    }
}

export default App;
