    //     const other = props.state.allTodoData.filter((obj) => obj.counterId != counterId);

    //    console.log(other);

    //    //setState({ ...props.state, allTodoData: other });
    // }

    //Get records count of clicked tab data
    var tabTypeBasedCount = props.state.allTodoData.filter((obj) => obj.todoType === tabType).length;
    if ((props.state.allTodoData.length > 0 && tabType == 'all')
        || (tabTypeBasedCount > 0)) {

        const { count } = props;
        console.log(props);

        return (
            //Looping through all data
            props.state.allTodoData.map((item, index) =>

                <Grid item xs={12} md={12} key={index.toString()}>
                    <Card className={classes.card}>

                        <CardActionArea>
                            {/*<CardMedia
                                            component="img"
                                            alt="Contemplative Reptile"
                                            height="140"
                                            image="/images/contemplative-reptile.jpg"
                                            title="Contemplative Reptile"
                             /> */ }
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="h2">
                                    {item.txtWhatToDo}  {item.txtWhenDiffFormat}

                                </Typography>
                                <Typography variant="body2" color="textSecondary" component="p">
                                    {item.txtWhereToDo}
                                </Typography>
                            </CardContent>
                        </CardActionArea>
                        <CardActions>
                            <IconButton size="small"
                                onClick={changeTodoToDone}>
                                <CheckIcon />
                            </IconButton>
                            <IconButton size="small"
                                onChange={()=> props.handleRemoval}
                            >
                                <DeleteIcon title="Delete Todo"/>
                            </IconButton>
                        </CardActions>
                    </Card>
                </Grid>

            )
        );
    }
    return <NoDataFound tabType={tabType} />;
}
